{"filter":false,"title":"caesar_debug-4.py","tooltip":"/os_subprocess/caesar_debug-4.py","undoManager":{"mark":7,"position":7,"stack":[[{"start":{"row":0,"column":0},"end":{"row":3,"column":0},"action":"remove","lines":["\"\"\"","Your module description","\"\"\"",""],"id":1},{"start":{"row":0,"column":0},"end":{"row":55,"column":24},"action":"insert","lines":["# Module Lab: Caesar Cipher Program Bug #4","#","# In a previous lab, you created a Caesar cipher program. This version of","# the program is buggy. Use a debugger to find the bug and fix it.","","# Double the given alphabet","def getDoubleAlphabet(alphabet):","    doubleAlphabet = alphabet + alphabet","    return doubleAlphabet","","# Get a message to encrypt","def getMessage():","    stringToEncrypt = input(\"Please enter a message to encrypt: \")","    return stringToEncrypt","","# Get a cipher key","def getCipherKey():","    shiftAmount = input(\"Please enter a key (whole number from 1-25): \")","    return shiftAmount","","# Encrypt message","def encryptMessage(message, cipherKey, alphabet):","    encryptedMessage = \"\"","    uppercaseMessage = \"\"","    uppercaseMessage = message.upper()","    for currentCharacter in uppercaseMessage:","        position = alphabet.find(currentCharacter)","        newPosition = position + int(cipherKey)","        if currentCharacter in alphabet:","            encryptedMessage = encryptedMessage + alphabet[newPosition]","        else:","            encryptedMessage = encryptedMessage + currentCharacter","    return encryptedMessage","","# Decrypt message","def decryptMessage(message, cipherKey, alphabet):","    decryptKey = -1 * int(cipherKey)","    return encryptMessage(message, decryptKey, alphabet)","","# Main program logic","def runCaesarCipherProgram():","    myAlphabet=\"ABCDEFGHIJKLMNOPQRSTUVWXYZ\"","    print(f'Alphabet: {myAlphabet}')","    myAlphabet2 = getDoubleAlphabet(myAlphabet)","    print(f'Alphabet2: {myAlphabet2}')","    myMessage = getMessage()","    print(myMessage)","    myCipherKey = getCipherKey()","    print(myCipherKey)","    myEncryptedMessage = encryptMessage(myMessage, myCipherKey, myAlphabet2)","    print(f'Encrypted Message: {myEncryptedMessage}')","    myDecryptedMessage = decryptMessage(myEncryptedMessage, myCipherKey, myAlphabet2)","    print(f'Decrypted Message: {myEncryptedMessage}')","","# Main logic","runCaesarCipherProgram()"]}],[{"start":{"row":17,"column":0},"end":{"row":17,"column":1},"action":"insert","lines":["2"],"id":3}],[{"start":{"row":17,"column":0},"end":{"row":17,"column":1},"action":"remove","lines":["2"],"id":4}],[{"start":{"row":52,"column":34},"end":{"row":52,"column":35},"action":"remove","lines":["E"],"id":5}],[{"start":{"row":52,"column":34},"end":{"row":52,"column":35},"action":"insert","lines":["D"],"id":6},{"start":{"row":52,"column":35},"end":{"row":52,"column":36},"action":"insert","lines":["e"]}],[{"start":{"row":52,"column":35},"end":{"row":52,"column":36},"action":"remove","lines":["e"],"id":8}],[{"start":{"row":52,"column":35},"end":{"row":52,"column":36},"action":"remove","lines":["n"],"id":9}],[{"start":{"row":52,"column":35},"end":{"row":52,"column":36},"action":"insert","lines":["e"],"id":10}]]},"ace":{"folds":[],"scrolltop":175,"scrollleft":0,"selection":{"start":{"row":52,"column":9},"end":{"row":52,"column":53},"isBackwards":true},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":{"row":11,"state":"start","mode":"ace/mode/python"}},"timestamp":1696992492474,"hash":"59ccac96631ec234ff7a94780656a56e25fb3b92"}